import { useState } from "react";
import Navbar from "./navbar";
import "../styles/addBooks.css"
import { Navigate } from "react-router-dom";

const AddBook = () => {
    let [title,setTitle]=useState("")
    let [pageCount,setPageCount]=useState("")
    let [authors,setAuthors]=useState("")
    let [categories,setCategories]=useState("")
    let [shortDescription,setShortDescription]=useState("")
    let [thumbnailUrl,setThumbnailUrl]=useState("")
    let naviagt
    
    let handlesubmit=(e)=>{
        e.preventDefault()
        let data=[title,pageCount,authors,categories,setShortDescription,thumbnailUrl]
        fetch('http://localhost:4000/books/',{
            method: 'POST',
            headers: { 'content-Type': 'application/json' },
            body: JSON.stringify(data)

        })
        alert("Book added successfully")

        Navigate("/book-list")
    }
    

    
    return (  
        <div className="addbook">
            <Navbar/>
            <div className="box1">
                <div className="title">
                <h1>Library Management System</h1>
                </div>
                <div className="forms">
                    <form action="" onSubmit={handlesubmit}>
                       
                    <label  style={{margin:"0px" ,padding:"40px"}}  htmlFor="">Title:</label>
                    <input type="text" value={title} onChange={(e)=>setTitle(e.target.value)} placeholder="enter title" />
                   <br />
                    <label  style={{margin:"0px" ,padding:"40px"}}  htmlFor="">Pages:</label>
                    <input type="text" value={pageCount} onChange={(e)=>setPageCount(e.target.value)} placeholder="enter the pagecount" />
                    <br />
                    <label  style={{margin:"0px" ,padding:"40px"}}  htmlFor="">Author:</label>
                    <input type="text" value={authors} onChange={(e)=>setAuthors(e.target.value)} placeholder="enter the author" />
                    <br />
                   <label   style={{margin:"0px" ,padding:"30px"}} htmlFor="">Category:</label>
                    <input type="text" value={categories} onChange={(e)=>setCategories(e.target.value)} placeholder="enter the category" />
                    <br />
                    <label  style={{margin:"0px" ,padding:"30px"}}  htmlFor="">Description:</label>
                    <input type="text" value={shortDescription} onChange={(e)=>setShortDescription(e.target.value)} placeholder="enter the Description" />
                    <br />
                    <label  style={{margin:"0px" ,padding:"40px"}}  htmlFor="">ImageUrl:</label>
                    <input type="text"value={thumbnailUrl} onChange={(e)=>setThumbnailUrl(e.target.value)} placeholder="enter the image url" />
                    
                    <br />
                    <br />
                    <button>Click</button>
                    <button>Cancel</button>
                    </form>

                </div>
            </div>
        </div>
    );
}
 
export default AddBook;
