import Navbar from "./navbar";
import { useState,useEffect } from "react";
import { useParams } from "react-router-dom";

const ReadBook = () => {
    let params=useParams()    //to fetch id from url
    

    let[book,setBook]=useState([])
    
    useEffect(()=>{
        let fetchData=async()=>{
            let response= await fetch(`http://localhost:4000/books/${params.id}`)
            let data= await response.json()
            setBook(data)

        }
        fetchData()
    })

    return ( 
        <div className="readbook">
           <Navbar/>
            <div className="book">
                {book.map((x)=>(
                    <div className="read">
                        <div className="url">
                            <img src={x.thumbnailUrl} alt=""/>
                        </div>
                        <div className="info">
                            <h1>Title:{x.title}</h1>
                            <h3>Authors:{x.authors}</h3>
                            <p>Short De</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default ReadBook;
