import { useEffect, useState } from "react";
import "../styles/content.css"
const Content = () => {
    let[data,setData]=useState([])
    useEffect(()=>{
            let fetchData = async() => {
            let response = await fetch("https://dummyjson.com/products")
            let data = await response.json()
            setData(data.products)
            console.log(data.products);
        }
        fetchData()
    })

    let search=(title)=>{}

   
    return ( 
    <div className="container">
        <h1 id="title">Employee Details</h1>
         {data.map((x)=>(
            <div className="innercont">
            <h1>Id:{x.id}</h1>
            <h1>Title:{x.title}</h1>
             <p>Description:{x.description}</p>
            <h3>Price:{x.price}</h3>
            <h3>Discount:{x.discountPercentage}</h3>
            <h3>Rating:{x.rating}</h3>
            <h2>Stock:{x.stock}</h2>
            <h2>Brand:{x.brand}</h2>
            <h2>Category{x.category}</h2>
            <h2>Thumbnail:{x.thumbnail}</h2>

            <button onClick={()=> search(data.title)}>{data.title}</button>

            <div className="image">
                <img src={x.images} alt=""/>
            </div>

        
            </div>  

        ))}
    </div>
 );
}
 
export default Content;
