import { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../styles/addData.css'

const Products = () => {
    let [id, setId] = useState([])
    let [title, setTitle] = useState([])
    let [price, setPrice] = useState([])
    let [rating, setRating] = useState([])
    let [brand,setBrand]=useState([])
   
    let navigate = useNavigate()

    let handleSubmit = (e) => {
        e.preventDefault()
        let data = { id,title,price,rating,brand }
        fetch("https://dummyjson.com/products", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data)
        })
        alert(" employee added successfully");
        navigate("/content")
    }
    return (
        <div className="container">
            <h1>FORMS</h1>
            <div className="forms">
                <form action="" onSubmit={handleSubmit}>
                    
                    <div className="info">
                    <h1>Id</h1>
                    <input type="number" value={id} onChange={(e) => setId(e.target.value)} placeholder="enter Id" />
                    <h1>Title</h1>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="enter the title" />
                    <h1>Price</h1>
                    <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="enter the price" />
                    <h1>Rating</h1>
                    <input type="number" value={rating} onChange={(e) => setRating(e.target.value)} placeholder="enter rating" />
                    <h1>Brand</h1>
                    <input type="text" value={brand} onChange={(e) => setBrand(e.target.value)} placeholder="enter Brand" />
                    <br />
                    <button>click</button>

                    <div className="image">
                        <img src="https://png.pngtree.com/thumb_back/fh260/back_our/20190619/ourmid/pngtree-company-business-background-poster-image_132990.jpg" alt=""/>
                    </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default P;
