import { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../styles/addData.css'

const AddData = () => {
    let [name, setName] = useState("")
    let [age, setAge] = useState("")
    let [role, setRole] = useState("")
    let [salary, setSalary] = useState("")
    let [company, setCompany] = useState("")
    let navigate = useNavigate()

    let handleSubmit = (e) => {
        e.preventDefault()
        let data = { name, age, role, salary, company }
        fetch("http://localhost:4000/company", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data)
        })
        alert(" employee added successfully");
        navigate("/content")
    }
    return (
        <div className="container">
            <h1>FORMS</h1>
            <div className="forms">
                <form action="" onSubmit={handleSubmit}>

                    <h1>Name</h1>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="enter the name" />
                    <h1>Age</h1>
                    <input type="number" value={age} onChange={(e) => setAge(e.target.value)} placeholder="enter the age" />
                    <h1>Role</h1>
                    <input type="text" value={role} onChange={(e) => setRole(e.target.value)} placeholder="enter the Role" />
                    <h1>Salary</h1>
                    <input type="number" value={salary} onChange={(e) => setSalary(e.target.value)} placeholder="enter the Salary" />
                    <h1>Company</h1>
                    <input type="text" value={company} onChange={(e) => setCompany(e.target.value)} placeholder="enter the company" />
                    <br />
                    <button>click</button>

                    <div className="image">
                        <img src="https://png.pngtree.com/thumb_back/fh260/back_our/20190619/ourmid/pngtree-company-business-background-poster-image_132990.jpg" alt=""/>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default AddData;
